function shuffleArray(array) {
  for (let i = array.length - 1; i > 0; i--) {
    const j = Math.floor(Math.random() * (i + 1));
    [array[i], array[j]] = [array[j], array[i]]; // Swap elements
  }
}

function initializeSurvey() {
  shuffledSpeciesKeys = Object.keys(speciesImages); // Assign keys to the array
  shuffleArray(shuffledSpeciesKeys); // Shuffle the array in place
  console.log("Shuffled species keys:", shuffledSpeciesKeys);
  displayRandomSpeciesPair(); // Now it should work correctly
}

function displayRandomSpeciesPair() {
  if (shuffledSpeciesKeys.length >= 2) {
    // Select two species randomly from shuffledSpeciesKeys and display their images
    const species1 = shuffledSpeciesKeys[0];
    const species2 = shuffledSpeciesKeys[1];
    currentSpeciesPair = [species1, species2];
    console.log("Selected species indexes: 0 and 1");
    console.log(`Displaying species pair: ${species1}, ${species2}`);
    showSpeciesPair(species1, species2);
  } else {
    console.error('Not enough species in the shuffled array to display a pair.');
  }
}

function getRandomImage(species) {
  const images = speciesImages[species];
  const randomImage = images[Math.floor(Math.random() * images.length)];
  console.log(`Random image for ${species}:`, randomImage);
  return randomImage;
}

function showSpeciesPair(species1, species2) {
  const img1 = getRandomImage(species1);
  const img2 = getRandomImage(species2);
  console.log(`Image paths set - Species 1: ${img1}, Species 2: ${img2}`);
  document.getElementById('image1Container').src = img1;
  document.getElementById('image2Container').src = img2;
}
// Assuming you have buttons with IDs 'regenerateSpecies1' and 'regenerateSpecies2'
document.getElementById('regenerateSpecies1').addEventListener('click', () => replaceImageForSpecies(0));
document.getElementById('regenerateSpecies2').addEventListener('click', () => replaceImageForSpecies(1));

function replaceImageForSpecies(speciesIndex) {
  const species = shuffledSpeciesKeys[speciesIndex];
  const newImg = getRandomImage(species);
  const containerId = speciesIndex === 0 ? 'image1Container' : 'image2Container';
  document.getElementById(containerId).src = newImg;
}

// Call initializeSurvey to start the survey when the script loads
document.addEventListener('DOMContentLoaded', (event) => {
  initializeSurvey();
});